import React from 'react';
import DefaultLayout from '../layout/DefaultLayout.tsx';
import { useState } from 'react';
import { useNavigate } from 'react-router-dom';
import { useAuth } from '../auth/AuthProvider.tsx';
import { API_URL } from '../auth/constants.ts';
import "./signup.css";
import Swal from 'sweetalert2';
import 'bootstrap/dist/css/bootstrap.min.css';
import axios from 'axios';

export default function Signup() {

    const navigate = useNavigate(); //Hook para la redireccion

    const [formData, setFormData] = useState({
        dni: "",
        NomyApe: "",
        email: "",
        direccion: "",
        telefono: "",
        codigo_localidad: "",
        password: ""
    });
    const [errors, setErrors] = useState<Record<string, string>>({});
    const [isSubmitting, setIsSubmitting] = useState(false);

    const validateForm = () => {
        const newErrors: Record<string, string> = {};
        
        // Validaci√≥n de DNI
        if (!formData.dni) newErrors.dni = 'DNI es requerido';
        else if (!/^\d{7,8}$/.test(formData.dni)) newErrors.dni = 'DNI debe tener 7 u 8 d√≠gitos';
        
        // Validaci√≥n de Nombre
        if (!formData.NomyApe) newErrors.NomyApe = 'Nombre completo es requerido';
        else if (formData.NomyApe.length < 5) newErrors.NomyApe = 'M√≠nimo 5 caracteres';
        
        // Validaci√≥n de Email
        if (!formData.email) newErrors.email = 'Email es requerido';
        else if (!/\S+@\S+\.\S+/.test(formData.email)) newErrors.email = 'Email no v√°lido';
        
        // Validaci√≥n de Direcci√≥n
        if (!formData.direccion) newErrors.direccion = 'Direcci√≥n es requerida';
        
        // Validaci√≥n de Tel√©fono
        if (!formData.telefono) newErrors.telefono = 'Tel√©fono es requerido';
        else if (!/^\d{10,15}$/.test(formData.telefono)) newErrors.telefono = 'Tel√©fono no v√°lido';
        
        // Validaci√≥n de Localidad
        if (!formData.codigo_localidad) newErrors.codigo_localidad = 'Localidad es requerida';
        
        // Validaci√≥n de Contrase√±a
        if (!formData.password) newErrors.password = 'Contrase√±a es requerida';
        else if (formData.password.length < 8) newErrors.password = 'M√≠nimo 8 caracteres';
        
        setErrors(newErrors);
        return Object.keys(newErrors).length === 0;
    };

    const handleChange = (e: React.ChangeEvent<HTMLInputElement>) => {
        const { name, value } = e.target;
        setFormData(prev => ({
            ...prev,
            [name]: value
        }));
    };

    async function handleSubmit(e: React.FormEvent<HTMLFormElement>) {
        e.preventDefault();

        if(!validateForm()) {
            console.log("Errores de validaci√≥n:", errors);
            return;
        };

        setIsSubmitting(true);

        try{
            const response = await axios.post(`${API_URL}/clientes/signup`, formData, {
                validateStatus: (status) => status < 400 // Acepta c√≥digos 200 y 201 como √©xitos
            });
            
            if (response.status === 201) {
                Swal.fire({
                    position: "center",
                    icon: "success",
                    title: "Bienvenido!",
                    text: "Usuario creado correctamente",
                    showConfirmButton: false,
                    timer: 1500
                });

                console.log("üì° Respuesta completa del backend:", response.data);
                console.log("üîë accessToken recibido:", response.data?.accessToken);
                console.log("üìÇ userData recibido:", response.data?.data);

                const userData = response.data?.data; // ‚úÖ Correcci√≥n
                const accessToken = response.data?.accessToken; // ‚úÖ Correcci√≥n
                const refreshToken = response.data?.refreshToken; // ‚úÖ Correcci√≥n

                if (!accessToken || !userData) {
                    console.error("‚ùå Error: El backend envi√≥ un accessToken o userData vac√≠o.");
                    return;
                }

                auth.login(accessToken, refreshToken, userData); // ‚úÖ Ahora se env√≠a correctamente

                // Redirigir al login o hacer login autom√°tico
                if (auth.isAuthenticated) {
                    console.log("üîç Estado de autenticaci√≥n despu√©s del signup:", auth.isAuthenticated);
                    const destino = auth.user?.rol === "cliente" ? "/homeCliente" : "/homePeluquero";
                    navigate(destino, {replace: true});
                };
            }else{
                console.log("Error al crear el usuario", response.data);
                Swal.fire({
                    icon: 'error',
                    title: 'Error',
                    text: 'Ha ocurrido un problema en el alta.',
                    confirmButtonText: 'Aceptar',
                    position: 'center'
                });
                // Manejar errores espec√≠ficos del backend
            };
        } catch(error: any){
            console.error("Error al crear usuario:", error.response?.data || error.message);
            Swal.fire({
                icon: "error",
                title: "Error",
                text: error.response?.data?.message || "Ha ocurrido un problema en el alta.",
                confirmButtonText: "Aceptar",
                position: "center"
            });
        } finally {
            setIsSubmitting(false);
        };
    };

    const auth = useAuth();
    
    if (auth.isAuthenticated) {
        const destino = auth.user?.rol === 'cliente' ? '/homeCliente' : '/homePeluquero';
